import React, { Component } from 'react';
import { RText } from '../../../../../components/Controls';
import CMSDatePicker from '../../../../../components/CMSDatePicker/CMSDatePicker';
import FormReactSelect from '../../../../../components/FormReactSelect';

class RowItemMoveDevice extends Component {
    onItemClickDelete = () => {
        if (!this.props.onItemClickDelete || typeof this.props.onItemClickDelete !== 'function') return;
        this.props.onItemClickDelete(this.props.index);
    }

    render() {
        var item = this.props.dataItem;
        var dataListProject = this.props.dataListProject;
        console.log("dataListProject: ", item);
        return (
            <div className="body-row" key={item.id}>
                <div className="body-col width10">
                    {(this.props.index) + 1}
                </div>
                <div className="body-col width25">
                    {item.id_device}
                </div>
                <div className="body-col width25">
                    {item.name}
                </div>

                <div className="body-col width25">
                    {item.serial_number}

                </div>
                <div className="body-col width15">
                    <FormReactSelect
                        instanceId="id_device_type"
                        className="id_device_type"
                        name="id_device_type"
                        required="required"
                        value={dataListProject.filter(function (v) { return v.id === item.id })}
                        onChange={(e) => { this.handleDropdownChange(e, 'id_device_type'); }}
                        optionList={dataListProject}
                    />
                </div>

            </div>
        );
    }
}
export default RowItemMoveDevice;
